.articles {
  background-color: $veryLightGray;
}

.article {

  &__content {

  }

  &__grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 1.875rem;

    @include breakpoint-up(medium){
      grid-template-columns: repeat(2, 1fr);
    }

    @include breakpoint-up(large){
      grid-template-columns: repeat(4, 1fr);
    }
  }

  &__item {
    border-radius: 0.3125rem;
    overflow: hidden;
    background-color: $white;
    box-shadow: 0px 3px 12px rgba(0,0,0,0.15);
    transition: all 150ms ease-in-out;

    &:hover {
      transform: scale(1.05);
    }
  }

  &__image {
    height: 12.5rem;
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center center;
  }

  &__text {
    padding: 1.875rem 1.875rem 2.5rem 1.875rem;
    color: $grayishBlue;

    @include breakpoint-up(medium){
      padding: 1.875rem 1.5625rem;
    }
  }

  &__author {
    font-size: 0.625rem;
    margin-bottom: 0.75rem;
  }

  &__title {
    font-size: 1.0625rem;
    line-height: 1.2;
    color: $darkBlue;
    margin-bottom: 0.5rem;
  }

  &__description {
    font-size: 0.8125rem;
  }
}

sources.app/scss/_footer.scss:
.footer {
  background-color: $darkBlue;
  color: $white;
  padding: 2.5rem;
  text-align: center;

  .container {

    @include breakpoint-up(large){
      display: grid;
      grid-template-columns: 2fr 1fr 1fr 3fr;
      grid-template-rows: 1fr auto;
      grid-template-areas: 
        "logo links1 links2 cta"
        "social links1 links2 copyright";
      gap: 1rem;
      justify-items: start;
    }
  }

  a {
    color: $white;
  }

  &__logo {
    display: inline-block;

    @include breakpoint-down(medium){
      margin-bottom: 1.875rem;
    }

    @include breakpoint-up(large){
      grid-area: logo;
    }
  }

  &__social {

    @include breakpoint-down(medium){
      margin-bottom: 1.875rem;
    }

    @include breakpoint-up(large){
      grid-area: social;
      align-self: end;
    }

    a {      
      display: inline-block;
      height: 1.25rem;

      svg path {
        transition: fill 150ms ease-in-out;
      }

      &:hover svg path {
        fill: $limeGreen;
      }
    }

    a:not(:last-child) {
      margin-right: 1rem;
    }

  }

  &__links {
    display: flex;
    flex-direction: column;
    align-items: center;
    font-size: 0.9375rem;

    @include breakpoint-up(large){
      justify-content: space-between;
      align-items: start;      
    }

    &.col1 {

      @include breakpoint-up(large){
        grid-area: links1;
      }
    }

    &.col2 {

      @include breakpoint-down(medium){
        margin-bottom: 1.875rem;
      }      

      @include breakpoint-up(large){
        grid-area: links2;
      }
    }

    a {
      line-height: 2.25;
      transition: color 150ms ease-in-out;

      &:hover {
        color: $limeGreen;
      }
    }
  }

  &__cta {

    @include breakpoint-up(large){
      grid-area: cta;
      text-align: right;
      justify-self: end;
    }

    a.button {

      @include breakpoint-down(medium){
        margin-bottom: 1.875rem; 
      }      

    }
  }

  &__copyright {
    font-size: 0.8125rem;
    color: $grayishBlue;

    @include breakpoint-up(large){
      grid-area: copyright;
      align-self: end;
      justify-self: end;
    }
  }

}
